import React, { useState } from "react";
import Modal from "react-modal";
//import Modal from "../components/Modal";

Modal.setAppElement("#root");

function About() {
  const hoursMinSecs = { minutes: 7, seconds: 0 };

  let subtitle;

  const [modalIsOpen, setIsOpen] = React.useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "#f00";
  }

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div class="tutorial">
      <div
        style={{
          border: "1px solid black",
          marginTop: "5%",
          marginLeft: "3%",
          marginRight: "3%",
        }}
      >
        <div style={{ marginLeft: "5%", marginTop: "2%", paddingBottom: "2%" }}>
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              paddingRight: "2%",
            }}
          >
            <div style={{ display: "flex", justifyContent: "inline" }}>
              <img
                style={{ paddingLeft: "0.5%" }}
                src={require("../images/Arrow.png")}
                class="img"
                alt="arrow icon"
              />
              <a
                style={{
                  textDecoration: "none",
                  color: "black",
                  paddingLeft: "10%",
                }}
                href="/try-it-yourself"
              >
                Back
              </a>
            </div>

            <div
              style={{
                display: "flex",
                justifyContent: "flex-start",
                alignItems: "center",
                paddingBottom: "1%",
              }}
            >
              <button tabindex="1" onClick={toggleModal} class="tutor-time-btn">
                7:00
              </button>
              <Modal
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
                onAfterOpen={afterOpenModal}
                contentLabel="Time Modal"
                className="mymodal"
                overlayClassName="myoverlay"
                closeTimeoutMS={500}
              >
                <div>Time Description</div>
                <button onClick={toggleModal}>Close modal</button>
              </Modal>
              <img
                style={{ paddingLeft: "0.5%" }}
                src={require("../images/Clock.png")}
                class="img"
                alt="clock icon"
              />
            </div>
          </div>

          <p
            style={{
              paddingBottom: "1%",
              paddingTop: "1%",
              paddingLeft: "2%",
              color: "#B3B3B3",
            }}
          >
            <button tabindex="1" onClick={toggleModal} class="tutor-btn">
              Category
            </button>
          </p>

          <h2
            style={{
              paddingBottom: "1%",
              fontWeight: "bold",
              paddingLeft: "2%",
            }}
          >
            <button tabindex="1" onClick={toggleModal} class="tutor-task-btn">
              Task title
            </button>
          </h2>
          {hoursMinSecs ? (
            <h3
              style={{
                paddingBottom: "1%",
                fontWeight: "normal",
                paddingLeft: "2%",
              }}
            >
              <button
                tabindex="1"
                onClick={toggleModal}
                class="tutor-description-btn"
              >
                Description
              </button>
            </h3>
          ) : null}
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            marginRight: "3%",
            marginBottom: "3%",
          }}
        >
          <a
            style={{
              border: "2px solid #e26c40",
              padding: "1%",
              color: "#e26c40",
              borderRadius: "15%",
              textDecoration: "none",
            }}
            href="/webcam"
          >
            Start Recording
          </a>
        </div>
      </div>
    </div>
  );
}

export default About;
