import { data } from "../data";
import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import CountDownTimer from "../components/Timer";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

function Task(props) {
  // Get ID from URL
  const { id } = useParams();
  const hoursMinSecs = { minutes: 5, seconds: 0 };
  const navigate = useNavigate();

  useEffect(() => {
    setTimeout(() => {
      navigate("/webcam");
      //1 second = 1000 millisecond
    }, 420000);
  });

  //<div>Task {id}</div>
  return (
    <div
      style={{
        border: "1px solid black",
        marginTop: "5%",
        marginLeft: "3%",
        marginRight: "3%",
      }}
    >
      <div style={{ marginLeft: "5%", marginTop: "2%", paddingBottom: "2%" }}>
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            paddingRight: "2%",
          }}
        >
          <div style={{ display: "flex", justifyContent: "inline" }}>
            <img
              style={{ paddingLeft: "0.5%" }}
              src={require("../images/Arrow.png")}
              class="img"
              alt="arrow icon"
            />
            <a
              style={{
                textDecoration: "none",
                color: "black",
                paddingLeft: "10%",
              }}
              href="/try-it-yourself"
            >
              Back
            </a>
          </div>

          <div
            style={{
              display: "flex",
              justifyContent: "flex-start",
              alignItems: "center",
              paddingBottom: "1%",
            }}
          >
            <CountDownTimer hoursMinSecs={hoursMinSecs} />
            <img
              style={{ paddingLeft: "0.5%" }}
              src={require("../images/Clock.png")}
              class="img"
              alt="clock icon"
            />
          </div>
        </div>

        <p
          style={{
            paddingBottom: "1%",
            paddingTop: "1%",
            paddingLeft: "2%",
            color: "#B3B3B3",
          }}
        >
          {data[id].category}
        </p>

        <h2
          style={{ paddingBottom: "1%", fontWeight: "bold", paddingLeft: "2%" }}
        >
          {data[id].title}
        </h2>

        <>
          <h3
            style={{
              paddingBottom: "1%",
              fontWeight: "normal",
              paddingLeft: "2%",
            }}
          >
            {data[id].description[0]}
          </h3>
          <h3
            style={{
              paddingBottom: "1%",
              fontWeight: "normal",
              paddingLeft: "2%",
            }}
          >
            {data[id].description[1]}
          </h3>
          <h3
            style={{
              paddingBottom: "1%",
              fontWeight: "normal",
              paddingLeft: "2%",
            }}
          >
            {data[id].description[2]}
          </h3>
        </>

        <div>
          <h3
            style={{
              paddingBottom: "1%",
              fontWeight: "bold",
              paddingLeft: "2%",
              paddingTop: "2%",
            }}
          >
            The problem you have chosen is:
          </h3>
          <h3
            style={{
              paddingBottom: "1%",
              fontWeight: "normal",
              paddingLeft: "2%",
            }}
          >
            {data[id].problem}
          </h3>
        </div>

        {data[id].imgURL !== "#" ? (
          <img
            style={{ height: "150px", paddingLeft: "2%" }}
            src={data[id].imgURL}
            alt="task visualization"
          />
        ) : null}

        <div
          style={{
            height: "200px",
            //backgroundColor: "purple",
            textAlign: "left",
          }}
        >
          <h3
            style={{
              paddingBottom: "1%",
              fontWeight: "bold",
              paddingLeft: "2%",
              paddingTop: "2%",
            }}
          >
            Your task is to do the following:
          </h3>
          <div class="task">
            <ul>
              <li>{data[id].task[0]}</li>
              <br></br>
              <li>{data[id].task[1]}</li>
              <br></br>
              <li>{data[id].task[2]}</li>
              <br></br>
              <li>{data[id].task[3]}</li>
            </ul>
          </div>
        </div>
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          marginRight: "3%",
          marginBottom: "3%",
        }}
      >
        {/* <a
          style={{
            border: "2px solid #e26c40",
            padding: "1%",
            color: "#e26c40",
            borderRadius: "10px",
            textDecoration: "none",
          }}
          href={`/webcam/${id}`}
        >
          Start Recording
        </a> */}
        <Link
          style={{
            border: "2px solid #e26c40",
            padding: "1%",
            color: "#e26c40",
            borderRadius: "10px",
            textDecoration: "none",
          }}
          to={`/webcam/${id}`}
        >
          Start Recording
        </Link>
      </div>
    </div>
  );
}

export default Task;
