import * as React from "react";
import data from "../data.json";
import { useParams } from "react-router-dom";
import CountDownTimer from "../components/Timer";

function Task() {
  // Get ID from URL
  const { id } = useParams();
  const hoursMinSecs = { minutes: 6 };

  //<div>Task {id}</div>
  return (
    <div
      style={{
        border: "1px solid black",
        marginTop: "5%",
        marginLeft: "3%",
        marginRight: "3%",
      }}
    >
      <div style={{ marginLeft: "5%", marginTop: "2%", paddingBottom: "2%" }}>
        <div></div>

        <p style={{ paddingBottom: "1%", paddingTop: "1%" }}>
          {data[id - 1].category}
        </p>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-start",
            alignItems: "center",
            paddingBottom: "1%",
          }}
        >
          <CountDownTimer hoursMinSecs={hoursMinSecs} />
          <img
            style={{ paddingLeft: "0.5%" }}
            src={require("../images/Clock.png")}
            class="img"
          />
        </div>

        <h2 style={{ paddingBottom: "1%", fontWeight: "bold" }}>
          {data[id - 1].title}
        </h2>
        <h2 style={{ paddingBottom: "1%", fontWeight: "normal" }}>
          {data[id - 1].description}
        </h2>
      </div>
    </div>
  );
}

export default Task;
